import React from 'react';
import marked from 'marked';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const initialState = `
This is a paragraph

**This is bolded text**

> Block Quotes!

# Heading
## Heading 2

-list item 1

-list item 2

-list item 3

[Visit my github](https://github.com/ArchonQ)

This is inline code \`<div></div>\`

This is a block of code
\`\`\`
  let x = 1;
  let y = 2;
  let z = x + y;
\`\`\`

![React](https://goo.gl/Umyytc)

`;

class App extends React.Component {
	state = {
		text: initialState,
	};

	handleChange = (e) => {
		this.setState({
			text: e.target.value,
		});
	};
	render() {
		const { text } = this.state;
		const markdown = marked(text, { breaks: true });

		return (
			<div className='container'>
				<h2 className='text-center m-4'>Convert your text</h2>
				<div className='row'>
					<div className='col-6'>
						<h6 className='text-center'>Enter your Markdown:</h6>
						<textarea
							className='form-control p-2'
							id='editor'
							value={text}
							onChange={this.handleChange}
						/>
					</div>

					<div className='col-6'>
						<h6 className='text-center'>See the result:</h6>
						<div
							className='preview rounded p-2'
							dangerouslySetInnerHTML={{ __html: markdown }}
							id='preview'
						/>
					</div>
				</div>
			</div>
		);
	}
}

export default App;
